mapper.select:
select user_idx, user_name, user_id, user_pwd, user_email, is_admin
from user
where user_id = #{user_id}

select user_idx, user_name, user_id, user_pwd, user_email, is_admin
from user
where user_email = #{user_email}

select user_id
from user
where user_email = #{user_email}

mapper.update:
update user
set user_pwd = #{user_pwd}
where user_email = #{user_email}

update user 

	user_name = #{user_name},
	user_pwd = #{user_pwd},
	user_email = #{user_email},
	id_admin = #{id_admin}

where user_id = #{user_id}

mapper.select:
select user_idx, user_name, user_id, user_pwd, user_email, is_admin
from user
where user_id = #{user_id} and user_pwd = #{user_pwd};

select user_idx, user_name, user_id, user_pwd, user_email, is_admin
from user
where user_id = #{user_id}

select token
from user
where user_id = #{user_id}

mapper.update:
update user
set token = #{token}
where user_id = #{user_id}

update user
set token = #{token, jdbcType=VARCHAR}
where user_id = #{user_id}
