mapper.select:
select user_idx, user_name, user_id, user_pwd, user_email, is_admin
from user
where user_id = #{user_id}

select user_idx, user_name, user_id, user_pwd, user_email, is_admin
from user
where user_email = #{user_email}

select user_idx, user_name, user_id, user_pwd, user_email, is_admin
from user
where user_idx = #{user_idx}

mapper.update:
update user 

	user_name = #{user_name},
	user_pwd = #{user_pwd},
	user_email = #{user_email},
	id_admin = #{id_admin}

where user_id = #{user_id}

